import React from "react";

function FavoriteProducts({ favoriteProductIds, products }) {
  // Filter the products based on the favorite product IDs
  const favoriteProducts = products.filter((product) =>
    favoriteProductIds.includes(product.productId)
  );

  return (
    <div>
      <h2 className="text-2xl font-semibold mb-4">Favorite Products</h2>
      <ul>
        {favoriteProducts.map((product) => (
          <li key={product.productId}>
            <div>
              <img
                src={product.imageUrl}
                alt={product.productName}
                className="w-16 h-16 object-cover object-center"
              />
            </div>
            <div>
              <h3 className="text-lg font-semibold">{product.productName}</h3>
              <p className="text-gray-500">${product.price}</p>
              {/* Add more product details as needed */}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default FavoriteProducts;
