import React, { useEffect, useState } from "react";
import axios from "axios";

function FavoriteProductsPage({ products }) {
  const [favoriteProducts, setFavoriteProducts] = useState([]);

  const fetchData = () => {
    var total = 0;
    var id = localStorage.getItem("id");
    axios
      .get(`https://ariz.onrender.com/api/fav/fav/${id}`)
      .then((res) => {
        console.log("API Response:", res.data);
        setFavoriteProducts(res.data.cart);
        for (let i = 0; i < res.data.cart.length; i++) {
          total += parseInt(res.data.cart[i].price);
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <div>
      <h2>Favorite Products</h2>
      {favoriteProducts && favoriteProducts.length > 0 ? (
        favoriteProducts.map((favProduct, index) => (
          <div
            key={index}
            class="mt-8 space-y-3 rounded-lg border bg-white px-2 py-4 sm:px-6 flex justify-between "
          >
            <div class="flex flex-col rounded-lg bg-white sm:flex-row">
              <img
                class="m-2 h-32 w-28 rounded-md border  object-center"
                src={favProduct.imageUrl}
                alt=""
              />
              <div class="flex w-full flex-col px-4 py-4">
                <span class="font-semibold">{favProduct.productName}</span>
                <span class="float-right text-sm font-medium text-gray-400">
                  Color : {favProduct.color}
                </span>
                <span class="float-right text-sm font-medium text-gray-400">
                  Size : {favProduct.size}
                </span>

                <p class="text-lg font-bold">Rs. {favProduct.price}</p>
              </div>
            </div>
            <div className="px-1  items-center">
              <button
                type="button"
                // onClick={() => handleDelete(fav._id)}
                className=" inline-flex items-center px-3 py-2  bg-red-500 hover:bg-red-700 text-white text-sm rounded-md"
              >
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  class="h-5 w-5 mr-1.5"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  <path
                    stroke-linecap="round"
                    stroke-linejoin="round"
                    stroke-width="2"
                    d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16"
                  />
                </svg>
                Remove
              </button>
            </div>
          </div>
        ))
      ) : (
        <p class="mt-4 text-gray-400">Your cart is empty.</p>
      )}
    </div>
  );
}

export default FavoriteProductsPage;
